import React, {Component} from 'react';
import './App.css';
import Clarifai from 'clarifai';
import FaceRecognition from './Components/FaceRecognition/FaceRecognition'
import Navigation from './Components/Navigation/Navigation';
import Logo from './Components/Logo/Logo';
import ImageLinkForm from './Components/ImageLinkForm/ImageLinkForm';
import Rank from './Components/Rank/Rank';
import Signin from './Components/Signin/Signin'

import AppParticles from './Components/Particles/Particles';
//import { useEffect, useMemo, useState } from "react";
//import Particles, { initParticlesEngine } from "@tsparticles/react";
//import { loadSlim } from "@tsparticles/slim";

const app = new Clarifai.App({
  apiKey: 'da25080a0f594fbfb117ffb83bcaaf1b'
 });

class App extends Component {

  constructor(){
    super();
    this.state={
      input: '',
      imageUrl: '',
      box: {},
      route: 'Signin'
    }
  }

  calculateFaceLocation =(data)=>{
    const clarifaiFace= data.outputs[0].data.regions[0].region_info.bounding_box
    const image= document.getElementById('inputImage');
    const width= Number(image.width);
    const height= Number(image.height);
    console.log(width, height);
    return{
      leftCol: clarifaiFace.left_col * width,
      topRow: clarifaiFace.top_row * height,
      rightCol: width - (clarifaiFace.right_col * width),
      bottomRow: height - (clarifaiFace.bottom_row * height),
    }
  }

  displayFaceBox= (box)=>{
    console.log(box);
    this.setState({box: box})
  }

  onInputChange= (event)=>{
    this.setState({input: event.target.value})
  }

  onButtonSubmit= ()=>{
    this.setState({imageUrl: this.state.input})
    app.models.predict(Clarifai.FACE_DETECT_MODEL, this.state.input).then(response=>this.displayFaceBox(this.calculateFaceLocation(response))
    .catch(err=>console.log(err))
    ); 
  }
  onRouteChange= ()=>{
    this.setState({route: 'home'});
  }
   render(){
      return (
        <div className="App">
          <AppParticles className='particles'
            //id="tsparticles"
            //particlesLoaded={particlesLoaded}
            //options={particleOptions}
          />

          <Navigation/>
          { this.state.route === 'Signin' 
            ? <Signin onRouteChange={this.setState.onRouteChange}/>
            : <div><Logo />
                  <ImageLinkForm 
                  onInputChange={this.onInputChange} 
                  onButtonSubmit={this.onButtonSubmit}/>
                  <Rank />
                  <FaceRecognition box={this.state.box} imageUrl={this.state.imageUrl}/>
          </div> }
        </div>
      );
    }


  };


export default App;
